<?phprequire("src/StringUtils.php");require("vendor/autoload.php");use Webmozart\Assert\Assert;use function StringUtils\capitalize;if (capitalize('') !== '') {    throw new \Exception('Функция работает неверно!');}Assert::eq(capitalize(''), ' ');if (capitalize('hello') !== 'Hello') {    throw new \Exception('Функция работает неверно!');}Assert::eq(capitalize('hello'), 'Hello');echo 'Все тесты пройдены!';